import React from 'react';

const About = () => {
  return (
    <div className="container mx-auto my-8">
      <h2 className="text-3xl font-bold mb-4">About Us</h2>
      <p className="text-lg">Our main idea is to provide luxury assisted living facilities for those who need it. We strive to offer a safe, comfortable, and homely environment with a variety of amenities designed to enhance the quality of life for our residents.</p>
    </div>
  );
};

export default About;
